# Written by @JamesHabben# Currently only works on hosts with PowerShell v2. PSv3+ causes a parsing error.# This can be deploy from a PSv3+ host as long as the host receiving this job is# a PSv2 host.$props = @{    'Name' = 'LF_csv';    'Filename' = 'C:\Users\public\Desktop\log.txt';    'Text' = 	'"%TargetInstance.Caption%",'+`				'"%TargetInstance.CommandLine%",'+`				'"%TargetInstance.CreationClassName%",'+`				'"%TargetInstance.CreationDate%",'+`				'"%TargetInstance.CSCreationClassName%",'+`				'"%TargetInstance.CSName%",'+`				'"%TargetInstance.Description%",'+`				'"%TargetInstance.ExecutablePath%",'+`				'"%TargetInstance.Handle%",'+`				'"%TargetInstance.HandleCount%",'+`				'"%TargetInstance.KernelModeTime%",'+`				'"%TargetInstance.MaximumWorkingSetSize%",'+`				'"%TargetInstance.MinimumWorkingSetSize%",'+`				'"%TargetInstance.Name%",'+`				'"%TargetInstance.OSCreationClassName%",'+`				'"%TargetInstance.OSName%",'+`				'"%TargetInstance.OtherOperationCount%",'+`				'"%TargetInstance.OtherTransferCount%",'+`				'"%TargetInstance.PageFaults%",'+`				'"%TargetInstance.PageFileUsage%",'+`				'"%TargetInstance.ParentProcessId%",'+`				'"%TargetInstance.PeakPageFileUsage%",'+`				'"%TargetInstance.PeakVirtualSize%",'+`				'"%TargetInstance.PeakWorkingSetSize%",'+`				'"%TargetInstance.Priority%",'+`				'"%TargetInstance.PrivatePageCount%",'+`				'"%TargetInstance.ProcessId%",'+`				'"%TargetInstance.QuotaNonPagedPoolUsage%",'+`				'"%TargetInstance.QuotaPagedPoolUsage%",'+`				'"%TargetInstance.QuotaPeakNonPagedPoolUsage%",'+`				'"%TargetInstance.QuotaPeakPagedPoolUsage%",'+`				'"%TargetInstance.ReadOperationCount%",'+`				'"%TargetInstance.ReadTransferCount%",'+`				'"%TargetInstance.SessionId%",'+`				'"%TargetInstance.ThreadCount%",'+`				'"%TargetInstance.UserModeTime%",'+`				'"%TargetInstance.VirtualSize%",'+`				'"%TargetInstance.WindowsVersion%",'+`				'"%TargetInstance.WorkingSetSize%",'+`				'"%TargetInstance.WriteOperationCount%",'+`				'"%TargetInstance.WriteTransferCount%"';} 